
public class Pattren1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i,j;
       for ( i=1; i<6; i++) 
       { 
           System.out.print("   "); 
       for (j=2*(6-i); j<=i; j++ )
       { 
           System.out.print(" * "); 
       } 

       System.out.println(); 
       }
 
 
	}

}
