
public class control {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int x=4;
        if(x%2==0)
        	System.out.println("the value is even");
        else
        	System.out.println("the value is odd");
        int num = 2;
        int i=0;
        while(i<10)
        {
        	System.out.println("even number,"+num);
        	num = num+2;
        	i++;
        }
	}

}
